import styled from 'styled-components';
import axios from 'axios';
import { useState } from 'react';
import button from "../../assets/Vector.png"


export default function CompleteHabits({id, name, done, currentSequence, highestSequence, token}){
    const [checkHabit, setCheckHabit] = useState('')
    const [ifDone, setIfDone] = useState(done)
    if(checkHabit !== '' && ifDone === false){
    const promise = axios.post(`https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits/${checkHabit}/check`,{},
        { 
            headers: {
                Authorization: `Bearer ${token}`
            }
        }

    );
    promise.then (response => {
        console.log(response);
    });

    promise.catch (error => {
        console.log(error);
    });
    setCheckHabit('')
    setIfDone(true)
    console.log("marquei")
    }
    if(checkHabit !== '' && ifDone === true){
        const promise = axios.post(`https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits/${checkHabit}/uncheck`,{},
        { 
            headers: {
                Authorization: `Bearer ${token}`
            }
        }

    );
    promise.then (response => {
        console.log(response);
    });

    promise.catch (error => {
        console.log(error);
    });
    setCheckHabit('')
    setIfDone(false)
    console.log("desmarquei")
    }

    function CheckHAbit(){
        setCheckHabit(id);
        console.log(ifDone);
        console.log(checkHabit);
    }

    return(
    <HabitCard>
        <div>
            <p className='title-card'>{name}</p>
            <p className='sequence'>SequÃªncia atual: {currentSequence} dias</p>
            <p className='sequence'>Seu recorde: {highestSequence} dias</p>
        </div>
        <Button onClick={CheckHAbit} banana = {ifDone}><img src={button}/></Button>
    </HabitCard>
    )
}

const HabitCard = styled.div`
    width: 340px;
    height: 94px;

    background: #FFFFFF;
    border-radius: 5px; 
    margin-bottom: 10px;
    padding: 13px;

    display: flex;
    justify-content: space-between;
    .title-card{
        font-size: 20px;
        line-height: 25px;

        color: #666666;
        margin-bottom: 7px;
    }
    .sequence{
        font-size: 13px;
        line-height: 16px;

        color: #666666;
    }

`
const Button = styled.button`
    border: none;
    border-radius: 5px;

    width: 69px;
    height: 69px;

    background-color: ${props => props.check};
`
