import Topo from "../Topo";
import Menu from "../Menu";
import styled from 'styled-components';
import { useState } from 'react';

export default function Habito({token, user}){
    const [createHabits, setCreatHabits] = useState("none");
    const [selectedDay, setSelectedDay] = useState ([]);
    const [habitName, setHabitName] = useState('');
    function selectDay(day){
        let days = day.target.id;
        if (selectedDay.includes(days)) {
            const filteredDays = selectedDay.filter(batata => batata !== days);
            setSelectedDay(filteredDays);
            return;
        }
        setSelectedDay([...selectedDay, days]);
        console.log(selectedDay);
    }
    return(
        <>
            <Topo user = {user}/>
            <Container>
                <div className="adicionar-habitos">
                    <p>Meus hábitos</p>
                    <button onClick={() => (createHabits === "none") ? setCreatHabits("list-itens"): setCreatHabits("none")}>+</button>
                </div>
                <RegisterHabits createHabits = {createHabits}>
                    <input placeholder='nome do hábito' type="text" onChange={(e) => setHabitName(e.target.value)} value={habitName} />
                    <Days>
                        <Button className={(selectedDay.includes("0")) && "selected"} id="0" onClick={selectDay}>D</Button>
                        <Button className={(selectedDay.includes("1")) && "selected"} id="1" onClick={selectDay}>S</Button>
                        <Button className={(selectedDay.includes("2")) && "selected"} id="2" onClick={selectDay}>T</Button>
                        <Button className={(selectedDay.includes("3")) && "selected"} id="3" onClick={selectDay}>Q</Button>
                        <Button className={(selectedDay.includes("4")) && "selected"} id="4" onClick={selectDay}>Q</Button>
                        <Button className={(selectedDay.includes("5")) && "selected"} id="5" onClick={selectDay}>S</Button>
                        <Button className={(selectedDay.includes("6")) && "selected"} id="6" onClick={selectDay}>S</Button>
                    </Days>
                    <Buttons>
                        <button onClick={() => setCreatHabits("none")} className="cancel">Cancelar</button>
                        <button className="save">Salvar</button>
                    </Buttons>
                </RegisterHabits>
                <Habits>

                </Habits>
            </Container>
            <Menu></Menu>
        </>
    );
}


const Container = styled.div`
    padding:17px;
    .adicionar-habitos{
        display: flex;
        justify-content: space-between;
        align-items: center;

        margin-bottom: 20px;
        p{
            font-size: 23px;
            line-height: 29px;

            color: #126BA5;
        }
        button{
            border: none;
            width: 40px;
            height: 35px;
            background-color: #52B6FF;
            border-radius: 5px;

            font-size: 27px;
            color: #FFFFFF;
        }
    }
`;

const RegisterHabits = styled.div`
    background: #FFFFFF;
    border-radius: 5px;
    padding: 19px;
    display: ${props => props.createHabits};
    input{
        background: #FFFFFF;
        border: 1px solid #D5D5D5;
        width: 100%;
        height: 45px;

        border-radius: 5px;

        font-size: 20px;
        line-height: 25px;
    }
`;

const Days = styled.div`
    margin-bottom: 30px;
    .selected{
        background-color: #CFCFCF;
        color: #FFFFFF;
    }
`;

const Button = styled.button`
    width: 30px;
    height: 30px;

    background-color: #FFFFFF;
    border: 1px solid #D5D5D5;
    border-radius: 5px;

    font-size: 20px;
    color: #DBDBDB;

    margin:8px 4px 0 0;
`;

const Buttons = styled.div`
    display: flex;
    justify-content: end;
    .cancel{
        font-size: 16px;
        color: #52B6FF;

        border: none;
        background-color: transparent;

        margin-right: 15px;
    }
    .save{
        width: 84px;
        height: 35px;

        background: #52B6FF;
        border-radius: 5px;

        font-size: 16px;
        color: #FFFFFF;

        border: none;
    }
`;

const Habits = styled.div`

`;
